mapper.select:
select
*, date_format(create_time, "%Y-%m-%d %T") as createTime_format
from
test_record



            record_id = #{recordId}


            id = #{id}

select
long_url as longUrl, short_url as shortUrl, short_flag as shortFlag, date_format(create_time, "%Y-%m-%d %T") as createTime
from
short_long_record



            long_url = #{longUrl}


            short_url = #{shortUrl}


            short_flag = #{shortFlag}

mapper.insert:
insert into
short_long_record(long_url, short_url, short_flag, create_time)
values(#{longUrl}, #{shortUrl}, #{shortFlag}, CURRENT_TIMESTAMP);
